import React, { FunctionComponent } from "react";
import { Button } from "@react-md/button";

import "./custom-interactions.scss";

const CustomInteractions: FunctionComponent = () => (
  <Button
    id="custom-state-button"
    enablePressedAndRipple
    className="custom-state-button"
  >
    Button
  </Button>
);

export default CustomInteractions;

src/custom-interactions.scss.content:
@import '@react-md/elevation/dist/mixins';
@import '@react-md/states/dist/mixins';

.custom-state-button {
  @include rmd-elevation-transition(8, 12, '&#{$rmd-states-pressed-class-name}', false);
  @include rmd-states-pressed-styles {
    // disable normal pressed states background color change since we want
    // to use elevation as the interaction instead
    @include rmd-states-theme-update-var(background-color, transparent);
  }
  margin: 1rem;
}
