@mixin react-md-overlay {
  .rmd-overlay {
    @include rmd-transition(standard);

    background-color: $rmd-overlay-color;
    bottom: 0;
    cursor: pointer;
    left: 0;
    opacity: 0;
    pointer-events: none;
    position: fixed;
    right: 0;
    top: 0;
    transition: opacity $rmd-overlay-transition-duration;
    z-index: $rmd-overlay-z-index;

    &--active {
      opacity: 1;
      pointer-events: auto;
    }
  }
}
