mixins.code:
@if not map-has-key($rmd-transitions, $type) {
  @error 'Invalid transition specified! \'#{type}\' doesn\'t exist. Choose one of #{map-keys($rmd-transitions)}';
}

transition-timing-function: map-get($rmd-transitions, $type);

.rmd-collapse {
  transition-property: max-height, padding-bottom, padding-top;
  will-change: max-height, padding-bottom, padding-top;

  &--no-overflow {
    overflow: hidden;
  }

  &--enter {
    @include rmd-transition($rmd-collapse-enter-transition-func);
  }

  &--leave {
    @include rmd-transition($rmd-collapse-leave-transition-func);
  }
}

variables.code:
$rmd-transitions: (
  sharp: cubic-bezier(.4, 0, .6, 1),
  standard: cubic-bezier(.4, 0, .2, 1),
  acceleration: cubic-bezier(.4, 0, 1, 1),
  deceleration: cubic-bezier(0, 0, .2, 1),
) !default;
