import React from "react";
import { render } from "react-dom";
import { StatesConfig, InteractionModeListener } from "@react-md/states";

import "./styles.scss";
import Demo from "./Demo";

const App = () => (
  <InteractionModeListener>
    <StatesConfig>
      <Demo />
    </StatesConfig>
  </InteractionModeListener>
);

render(<App />, document.getElementById("root"));

src/AutoDense.scss.content:
@import '~@react-md/app-bar/dist/mixins';
@import '~@react-md/utils/dist/mixins';

.auto-dense {
  @include rmd-utils-desktop-media {
    @include rmd-app-bar-theme-update-var(height, rmd-app-bar-theme-var(dense-height));

    &__prominent {
      @include rmd-app-bar-theme-update-var(
        prominent-height,
        rmd-app-bar-theme-var(prominent-dense-height)
      );
    }
  }

  &__prominent {
    @include rmd-app-bar-theme-update-var(
      prominent-height,
      rmd-app-bar-theme-var(prominent-dense-height)
    );

    // just because I don't want to create 2 rows of data for this example
    align-items: flex-start;
  }
}

