@import '~@react-md/utils/dist/mixins';

.app-bar-container {
  @include rmd-utils-block-centered;
  @include rmd-utils-phone-media {
    margin: 2rem 0.5rem;
    max-width: none;
  }

  display: grid;
  grid-row-gap: 2rem;
  max-width: 40rem;
}

