import React, { FC } from "react";
import {
  TableContainer,
  Table,
  TableHeader,
  TableRow,
  TableCell,
  TableBody,
  TableFooter,
} from "@react-md/table";

const columns = Array.from(new Array(10), (_, i) => `Column ${i + 1}`);

const Demo: FC = () => (
  <TableContainer style={{ maxHeight: "20rem" }}>
    <Table fullWidth>
      <TableHeader sticky>
        <TableRow>
          {columns.map(column => (
            <TableCell key={column}>{column}</TableCell>
          ))}
        </TableRow>
      </TableHeader>
      <TableBody>
        {Array.from(new Array(20), (_, i) => (
          <TableRow key={i}>
            {columns.map((_, j) => (
              <TableCell key={j}>{`Row ${i + 1} Cell ${j + 1}`}</TableCell>
            ))}
          </TableRow>
        ))}
      </TableBody>
      <TableFooter sticky>
        <TableRow>
          <TableCell colSpan="100%">
            This is the sticky footer content. Any components can be rendered
            inside.
          </TableCell>
        </TableRow>
      </TableFooter>
    </Table>
  </TableContainer>
);

export default Demo;
